import React from "react";
import styled from 'styled-components';
import axios from "axios";

const url =
  "https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users";

const headers = {
  headers: {
    Authorization: "barbara-costa-moreira"
  }
};


class App extends React.Component{
  state = {
    Nome: "",
    Email: "",
    Usuarios: []
  }

  componentDidMount() {
    this.getAllUsers()
  }

  getAllUsers = () => {
    axios
    .get(url, headers)
    .then((resposta) =>{this.setState({Usuarios: resposta.data})})
    .catch((erro) => {alert(`Algo deu errado!`)})

  }

  createUsers = () =>{
    const body = {
      name: this.state.Nome,
      email: this.state.Email
    }
    axios
    .post(url,body,headers)
    .then ((resposta) =>{
      alert(`O usuário ${this.state.Nome} foi cadastrado com sucesso!`)
      this.setState({
        Nome:"",
        Email:""
    })
    this.getAllUsers()
    })
    .catch((erro) =>{
      alert(erro.response.data.message)
      this.setState({
        Nome:"",
        Email:""
      })
    })
  }

  upDateNome = (ev) =>{
    this.setState({
      Nome: ev.target.value
    })
  }

  upDateEmail = (ev) =>{
    this.setState({
      Email: ev.target.value

    })
  }

render() {
  // console.log(this.state.Nome, this.state.Email)
  const usuariosLista = this.state.Usuarios.map((usuario) => {
    return <p key={usuario.id}>{usuario.name}</p>
  })
  return (
  <div>
    <div>
      <button>Troca de Tela</button>
    </div>

    <div>
      <input 
      value={this.state.Nome}
      onChange={this.upDateNome}
      placeholder = 'Nome'/>
      <input 
      value={this.state.Email}
      onChange={this.upDateEmail}
      placeholder = 'E-mail'/> 
      <button onClick={this.createUsers}>Criar Usuário</button>
      {usuariosLista}
    </div>


  </div>
  )
}

}

export default App;
