import React from 'react'
import styled from 'styled-components'
import './styles.css'

const TarefaList = styled.ul`
  padding: 0;
  width: 200px;
`

const Tarefa = styled.li`
  text-align: left;
  text-decoration: ${({completa}) => (completa ? 'line-through' : 'none')};
`

const InputsContainer = styled.div`
  display: grid;
  grid-auto-flow: column;
  gap: 10px;
`

class App extends React.Component {
    state = {
      tarefas: [{
        id: Date.now(), 
        texto: 'Caminhar',
        completa: false
      },
      {
        id: Date.now()+1, 
        texto: 'Preparar café da manhã',
        completa: true
      }],
      inputValue: '',
      filtro: ''
    }

    // salvarLocalStorage = () => {
    //   localStorage.setItem("tarefas", JSON.stringify(this.state.tarefas));
    //   // localStorage.setItem("inputValue", this.state.inputValue);
      // localStorage.setItem("filtro", this.state.filtro);
    // };
    // buscarLocalStorage = () => {
    //   const tarefas = localStorage.getItem("tarefas", JSON.parse(this.state.tarefas) );
    //   const inputValue = localStorage.getItem("inputValue");
    //   const filtro = localStorage.getItem("filtro");

    //   this.setState({
    //     tarefas: [{
    //       id: "", 
    //       texto: "",
    //       completa: ""
    //     }],

    //   inputValue: '',
    //   filtro: ''
    // })
    // }

  componentDidUpdate = () => {
    localStorage.setItem("tarefas", JSON.stringify(this.state.tarefas));
    // this.salvarLocalStorage();
  }


  componentDidMount= () => {
    const tarefasString = JSON.parse(localStorage.getItem("tarefas"))
    this.setState({ 
      tarefas: tarefasString || ""});  
  }


  onChangeInput = (event) => {
    this.setState({
      inputValue: event.target.value})
  }

  criaTarefa = (event) => {
    const novaTarefa = {
      id: Date.now(), 
      texto: this.state.inputValue,
      completa: false
    }
    const copiaListaDeTarefas = [...this.state.tarefas, novaTarefa]
    this.setState({tarefas: copiaListaDeTarefas, inputValue: ""})

  }

  selectTarefa = (id) => {
    const copiaListaDeTarefas = this.state.tarefas.map((tarefa) => {
      if(id === tarefa.id) {
        const novaTarefa = {
          ...tarefa,
          completa: !tarefa.completa
        }
        return novaTarefa
      } else {
        return tarefa
      }
    })

    this.setState({tarefas: copiaListaDeTarefas})
  }

  onChangeFilter = (event) => {
    this.setState({
      filtro: event.target.value})

  }

  render() {
    const listaFiltrada = this.state.tarefas.filter(tarefa => {
      switch (this.state.filtro) {
        case 'pendentes':
          return !tarefa.completa
        case 'completas':
          return tarefa.completa
        default:
          return true
      }
    })

    return (
      <div className="App">
        <h1>Lista de tarefas</h1>
        <InputsContainer>
          <input value={this.state.inputValue} onChange={this.onChangeInput}/>
          <button onClick={this.criaTarefa}>Adicionar</button>
        </InputsContainer>
        <br/>

        <InputsContainer>
          <label>Filtro</label>
          <select value={this.state.filtro} onChange={this.onChangeFilter}>
            <option value="">Nenhum</option>
            <option value="pendentes">Pendentes</option>
            <option value="completas">Completas</option>
          </select>
        </InputsContainer>
        <TarefaList>
          {listaFiltrada.map(tarefa => {
            return (
              <Tarefa
                completa={tarefa.completa}
                onClick={() => this.selectTarefa(tarefa.id)}
              >
                {tarefa.texto}
              </Tarefa>
            )
          })}
        </TarefaList>
      </div>
    )
  }
}

export default App
