import React from "react";
import axios from "axios";
import styled from "styled-components";
import AddMusica from "./components/AddMusicas"
import ListaPlayList from "./components/ListaPlayList"


class App extends React.Component {
  state = {
    telaAtiva: "App"
  }


  trocaTela = () => {
    switch (this.state.telaAtiva){
      case "AdicionarMusica":
        return <AddMusica/>
      case "ListarPlayList": 
        return <ListaPlayList/>
      default:
        return <App/>

    }
  }



  render(){
    return(
      <div>
        <div>
          <h1>
             Crie sua Playlist Personalizada!
          </h1> 
          <input
          placeholder="Digite o nome"/>
          <button>Adicionar</button>
        </div>
        <div>
          <button onClick={<ListaPlayList/>}>Listar Playlists existentes </button>
          <button onClick={<AddMusica/>}>Listar Playlists existentes </button>
        </div>

      </div>
    )
  }
}


export default App;
