import React from "react";
import axios from "axios";
import styled from "styled-components";
import AddMusicas from "./components/AddMusicas"
import ListaPlayList from "./components/ListaPlayList"
import Home from "./components/Home"


class App extends React.Component {

  state = {
    telaAtiva:"Home"
  }


  trocaTela = () => {
    switch (this.state.telaAtiva){
      case "AdicionarMusica":
        return <AddMusicas/>
      case "ListarPlayList": 
        return <ListaPlayList/>
      default:
        return <Home/>

    }
  }

  // funcaoMudarTela = (nomeDaTela) => {
  //   this.setState ({telaAtiva: nomeDaTela})
  // }
  irAddMusica = () => {
    this.setState ({telaAtiva: "AdicionarMusica"})
  }
  irListaPlay = () => {
    this.setState ({telaAtiva: "ListarPlayList"})
  }


  render(){
    return(
      <div>
       {this.Home}
      </div>
    )
  }
}

export default App;
