import ChooseButton from "./components/ChooseButtons";
import { useState } from "react";


const BoxContainer = styled.div`
  Border: 1px solid black;
  display: flex;
  justify-content: center;
`



const App = () => {
  const [telaAtual, setTelaAtual] = useState(<AppBar/>)

  const trocaTela = ()=>{
    switch(telaAtual) {
      case 'Seus Matchs':
        return <ChooseProfilePage/>
      case 'Escolher Perfis':
        return <AppBar botao={atualizaTela}/>
      default: 
        return <AppBar/>
    }
  }

    const atualizaTela = ()=>{
      setTelaAtual ("Escolher Perfis")
    }

  return (
    <BoxContainer >
      <AppBar/>
      {/* {trocaTela()} */}
    </BoxContainer>
  );
}
