import React, {useState, useEffect} from "react";
import styled from "styled-components";
import sim from "./img/sim.png";
import nao from "./img/nao.png"

const Botao = styled.button`
    // display: flex;
    border-radius: 3px;
    height: 70px;
    width: 70px;
`
const ContainerBotao = styled.div`
    // height: 70px;
    // width: 70px;
    font-size: 40px;
    // border-radius: 50px;
    // border-color: red;
    // color:red;
    // cursor: pointer;
    // &:hover{
    //     font-size: 50px;
    //     background-color: red;
    //     color: white;
    // }
`

const ChooseButton = () => {
  return (
    <ContainerBotao>
    Sou o ChooseButton
        <Botao>
            <img src={sim}/>
        </Botao>
        <button>
            <img src={nao} />
        </button>
    </ContainerBotao>
  );
}

export default ChooseButton;
